import React from "react"

class App extends React.Component
{
	render() {
		return (
			<form id="contact-form" onSubmit={this.handleSubmit.bind(this)} method="POST">
				<div className="form-group">
					<label htmlFor="name">Name</label>
					<input type="text" className="form-control" name="name"/>
				</div>
				<div className="form-group">
					<label htmlFor="exampleInputEmail1">Email address</label>
					<input type="text" className="form-control" name="email" aria-describedby="emailHelp"/>
				</div>

				<div className="form-group">
					<label htmlFor="message">Message</label>
					<textarea className="form-control" rows="5" name="message"></textarea>
				</div>

				<button type="submit" className="btn btn-primary">Submit</button>
			</form>
		)
	}

	handleSubmit(event)
	{
		event.preventDefault();
		const inputData = new FormData(event.target);
		const name = inputData.get('name');
		const email = inputData.get('email');
		const message = inputData.get('message');
		fetch('/routes/users',
			{
				method: 'POST',
				mode: "cors",
				body: JSON.stringify({
					name: name,
					email: email,
					message: message
				}),
				headers: {'Content-Type': 'application/json'}
			});
		this.resetForm();
	}

	resetForm(){
		document.getElementById('contact-form').reset();
	}
}

export default App;
